import {Link, Route, useParams, useRouteMatch} from "react-router-dom";
import {Fragment} from "react";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const DUMMY_QUOTES = [
    {id:'q1',author: 'Nelson Mandela', text: 'The greatest glory in living lies not in never falling, but in rising every time we fall.'},
    {id:'q2',author: 'Steve Jobs', text: 'Your time is limited, so don\'t waste it living someone else\'s life. Don\'t be trapped by dogma – which is living with the results of other people\'s thinking.'},
    {id:'q3',author: 'John Lennon', text: 'Life is what happens when you\'re busy making other plans.'},
];

const QuoteDetail = () => {
    const match = useRouteMatch();
    const params = useParams();

    console.log(match);

    const quote = DUMMY_QUOTES.find(quote=>quote.id===params.quoteId)

    if (!quote){
        return <p>No quote found!</p>
    }

    return (
        <Fragment>
            <HighlightedQuote text={quote.text} author={quote.author}/>
            <Route path={`/quotes/${params.quoteId}`} exact>
                <div className='centered'>
                    <Link className='btn--flat' to={`/quotes/${params.quoteId}/comments`}>
                        Load Comments
                    </Link>
                </div>
            </Route>
            <Route path={`/quotes/${params.quoteId}/comments`}>
                <Comments></Comments>
            </Route>
        </Fragment>
    );
}

export default QuoteDetail;
